 checkP2pAccess # Description # Tool to check peer-to-peer connectivity between GPUs connected to the motherboard.
Code (C++) # #include &lt;stdio.h&gt; int main() { int nDevices; cudaGetDeviceCount(&amp;nDevices); printf(&#34;Number of CUDA-capable devices: %d
&#34;, nDevices); cudaDeviceProp prop; for (int i = 0; i &lt; nDevices; ++i) { cudaGetDeviceProperties(&amp;prop, i); printf(&#34;Device %d: %s
&#34;, i, prop.name); } int p2pOK[nDevices][nDevices]; for (int i = 0; i &lt; nDevices; ++i) { for (int j = i+1; j &lt; nDevices; ++j) { cudaDeviceCanAccessPeer(&amp;p2pOK[i][j], i, j); p2pOK[j][i] = p2pOK[i][j]; } } printf(&#34;
&#34;); for (int i = 0; i &lt; nDevices; ++i) { printf(&#34; %3d&#34;, i); } printf(&#34;
&#34;); for (int j = 0; j &lt; nDevices; ++j) { printf(&#34;%3d&#34;,j); for (int i = 0; i &lt; nDevices; ++i) { if (i==j) { printf(&#34; - &#34;); } else if (p2pOK[i][j] == 1) { printf(&#34; Y &#34;); } else { printf(&#34; &#34;); } } printf(&#34;
&#34;); } } Code (Fortran) # program checkP2pAccess use cudafor implicit none integer, allocatable:: p2pOK(:,:) integer:: nDevices, i, j, istat type (cudaDeviceProp):: prop istat = cudaGetDeviceCount(nDevices) write(*,&#34;(&#39;Number of CUDA -capable devices: &#39;, i0,/)&#34;) nDevices do i = 0, nDevices -1 istat = cudaGetDeviceProperties(prop, i) write(*,&#34;(&#39;Device &#39;, i0, &#39;: &#39;, a)&#34;) i, trim(prop%name) end do write(*,*) allocate(p2pOK (0: nDevices -1, 0: nDevices -1)) p2pOK = 0 do j = 0, nDevices -1 do i = j+1, nDevices -1 istat = cudaDeviceCanAccessPeer(p2pOK(i,j), i, j) p2pOK(j,i) = p2pOK(i,j) end do end do do i = 0, nDevices -1 write(*,&#34;(3x,i3)&#34;, advance=&#39;no&#39;) i end do write(*,*) do j = 0, nDevices -1 write(*,&#34;(i3)&#34;, advance=&#39;no&#39;) j do i = 0, nDevices -1 if (i == j) then write(*,&#34;(2x,&#39;-&#39;,3x)&#34;, advance=&#39;no&#39;) else if (p2pOK(i,j) == 1) then write(*,&#34;(2x, &#39;Y&#39;,3x)&#34;,advance=&#39;no&#39;) else write(*,&#34;(6x)&#34;,advance=&#39;no&#39;) end if end do write(*,*) end do end program checkP2pAccess

 p2pBandwidth # Description # Program to measure the bandwidth of memory transfer between a GPUs on a multi-GPU host.
A useful technique shown here, is the use of derived types with device member arrays to manage each GPU&rsquo;s instance of the device arrays.
Code (C++) # #include &lt;stdio.h&gt; #include &lt;cstdlib&gt; struct distributedArray { float* a_d; }; __global__ void setVal(float* __restrict__ array, float val) { int i = threadIdx.x + blockIdx.x * blockDim.x; array[i] = val; } int main() { const int N = 4*1024*1024; const size_t nbytes = N*sizeof(float); // displaying number of GPUs int nDevices; cudaGetDeviceCount(&amp;nDevices); // displaying GPU names cudaDeviceProp prop; for (int i = 0; i &lt; nDevices; ++i) { cudaGetDeviceProperties(&amp;prop, i); printf(&#34;Device %d: %s
&#34;, i, prop.name); } printf(&#34;
&#34;); distributedArray distArray[nDevices]; // enable p2p access between GPUs (if possible) int access; for (int j = 0; j &lt; nDevices; ++j) { // sets GPU cudaSetDevice(j); // allocate device array on the set GPU cudaMalloc(&amp;distArray[j].a_d, nbytes); setVal&lt;&lt;&lt;N/1024, 1024&gt;&gt;&gt;(distArray[j].a_d, j); for (int i = j+1; i &lt; nDevices; ++i) { cudaDeviceCanAccessPeer(&amp;access, j, i); if (access == 1) { cudaSetDevice(j); cudaDeviceEnablePeerAccess(i, 0); cudaSetDevice(i); cudaDeviceEnablePeerAccess(j, 0); } } } // allocating array to record inter-GPU bandwidths float bandwidth[nDevices][nDevices]; for (int i = 0; i &lt; nDevices; ++i) { for (int j = 0; j &lt; nDevices; ++j) { bandwidth[i][j] = 0.0; } } // measure p2p bandwidth between each pair of GPUs cudaEvent_t startEvent, stopEvent; float time; float *array = (float*)malloc(nbytes); for (int j = 0; j &lt; nDevices; ++j) { cudaSetDevice(j); cudaEventCreate(&amp;startEvent); cudaEventCreate(&amp;stopEvent); for (int i = 0; i &lt; nDevices; ++i) { if (i == j) continue; cudaEventRecord(startEvent, 0); cudaMemcpyPeer(distArray[j].a_d, j, distArray[i].a_d, i, nbytes); cudaEventRecord(stopEvent, 0); cudaEventSynchronize(stopEvent); cudaEventElapsedTime(&amp;time, startEvent, stopEvent); cudaMemcpy(array, distArray[j].a_d, nbytes, cudaMemcpyDeviceToHost); for (int k = 0; k &lt; N; ++k) { if (array[k] != i) { printf(&#34;Transfer between GPUs %d and %d failed!
&#34;, j, i); std::exit(1); } } bandwidth[j][i] = N*sizeof(float)/time/1.0e6; } setVal&lt;&lt;&lt;N/1024, 1024&gt;&gt;&gt;(distArray[j].a_d, j); cudaEventDestroy(startEvent); cudaEventDestroy(stopEvent); } printf(&#34;Bandwidth (GB/s) for transfer size (MB): %f
&#34;, nbytes/1024.0/1024.0); printf(&#34; S\R 0&#34;); for (int i = 1; i &lt; nDevices; ++i) printf(&#34; %3d&#34;, i); printf(&#34;
&#34;); for (int j = 0; j &lt; nDevices; ++j) { printf(&#34;%3d&#34;, j); for (int i = 0; i &lt; nDevices; ++i) { if (i==j) { printf(&#34; 0 &#34;); } else { printf(&#34;%8.2f&#34;, bandwidth[j][i]); } } printf(&#34;
&#34;); } // cleanup for (int j = 0; j &lt; nDevices; ++j) { cudaFree(distArray[j].a_d); } } Code (Fortran) # program p2pBandwidth use cudafor implicit none integer, parameter:: N = 4*1024*1024 type distributedArray real, device, allocatable:: a_d(:) end type distributedArray type(distributedArray), allocatable:: distArray(:) real, allocatable:: bandwidth(:,:) real:: array(N), time integer:: nDevices, access, i, j, istat type(cudaDeviceProp):: prop type(cudaEvent):: startEvent, stopEvent ! displaying number of GPUs istat = cudaGetDeviceCount(nDevices) write(*,&#34;(&#39;Number of CUDA-capable devices: &#39;, i0,/)&#34;) nDevices ! displaying GPU names do i = 0, nDevices-1 istat = cudaGetDeviceProperties(prop, i) write(*,&#34;(&#39;Device &#39;, i0, &#39;: &#39;, a)&#34;) i, trim(prop%name) end do write(*,*) ! creating device array for each GPU allocate(distArray(0:nDevices-1)) ! enable p2p access between GPUs (if possible) do j = 0, nDevices-1 istat = cudaSetDevice(j) ! sets GPU allocate(distArray(j)%a_d(N)) ! allocates device array on the set GPU distArray(j)%a_d = j do i = j+1, nDevices-1 istat = cudaDeviceCanAccessPeer(access, j, i) if (access == 1) then istat = cudaSetDevice(j) istat = cudaDeviceEnablePeerAccess(i, 0) istat = cudaSetDevice(i) istat = cudaDeviceEnablePeerAccess(j, 0) end if end do end do ! allocating array to record inter-GPU bandwidths allocate(bandwidth(0:nDevices-1, 0:nDevices-1)) bandwidth(:,:) = 0. ! measure p2p bandwidth between each pair of GPUs do j = 0, nDevices-1 istat = cudaSetDevice(j) istat = cudaEventCreate(startEvent) istat = cudaEventCreate(stopEvent) do i = 0, nDevices-1 if (i == j) cycle istat = cudaMemcpyPeer(distArray(j)%a_d, j, distArray(i)%a_d, i, N) istat = cudaEventRecord(startEvent, 0) istat = cudaMemcpyPeer(distArray(j)%a_d, j, distArray(i)%a_d, i, N) istat = cudaEventRecord(stopEvent, 0) istat = cudaEventSynchronize(stopEvent) istat = cudaEventElapsedTime(time, startEvent, stopEvent) array = distArray(j)%a_d if (all(array == i)) bandwidth(j,i) = N*4/time/1.0E+6 end do distArray(j)%a_d = j istat = cudaEventDestroy(startEvent) istat = cudaEventDestroy(stopEvent) end do write(*,&#34;(&#39;Bandwidth (GB/s) for transfer size (MB): &#39;, f9.3,/)&#34;) N*4./1024**2 write(*,&#34;(&#39; S\R 0&#39;)&#34;, advance=&#39;no&#39;) do i = 1, nDevices-1 write(*,&#34;(5x,i3)&#34;, advance=&#39;no&#39;) i end do write(*,*) do j = 0, nDevices-1 write(*,&#34;(i3)&#34;, advance=&#39;no&#39;) j do i = 0, nDevices-1 if (i==j) then write(*,&#34;(4x,&#39;0&#39;,3x)&#34;, advance=&#39;no&#39;) else write(*,&#34;(f8.2)&#34;, advance=&#39;no&#39;) bandwidth(j,i) end if end do write(*,*) end do ! cleanup do j = 0, nDevices-1 deallocate(distArray(j)%a_d) end do deallocate(distArray, bandwidth) end program p2pBandwidth
